@use 'reset' as *;

:root {
  /* Variables */
}

#index {
  header {
    height: 100vh;
    display: flex;
    align-items: center;
    background: linear-gradient(to top left, #28b487, #7dd56f);

    h1 {
      font-family: sans-serif;
      font-size: 50px;
      line-height: 1.3;
      width: 100%;
      padding: 30px;
      text-align: center;
      color: white;
    }
  }

  main {
    display: flex;
    justify-content: space-between;
    padding: 50px;
    font-family: Arial;
    font-size: 20px;
    background-color: rgb(255, 247, 201);

    article {
      width: 60%;

      ul li {
        list-style-type: disc;
      }
    }

    aside {
      width: 30%;

      h2 {
        font-size: 35px;
        margin-bottom: 25px;
      }

      h3 {
        margin-bottom: 20px;
        text-align: center;
      }

      p {
        margin-bottom: 20px;
      }

      .first {
        color: red;
      }

      #your-name {
        background-color: rgb(255, 220, 105);
        border: 5px solid #444;
        width: 400px;
        padding: 25px;
        margin-top: 30px;
      }

      input,
      button {
        padding: 10px;
        font-size: 16px;
      }

      a {
        background-color: yellowgreen;
      }

      #course-image {
        width: 300px;
      }

      #your-name h2 {
        color: olivedrab;
      }
    }
  }
}

